import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Filter } from 'bad-words';
import styles from './styles/sendfeedback.module.css';

const SendFeedback = () => {
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [status, setStatus] = useState('');
  const [submitted, setSubmitted] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const userEmail = params.get('email');
    setEmail(userEmail);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!message.trim()) {
      setStatus('Please enter a message.');
      return;
    }

    setIsLoading(true);
    const filter = new Filter();
    const cleanedMessage = filter.clean(message);

    try {
      const res = await axios.post('http://localhost:5000/api/feedback/send', {
        email,
        message: cleanedMessage,
      });
      setStatus(res.data.message);
      setSubmitted(true);
    } catch (error) {
      setStatus(error.response?.data?.message || 'Failed to send feedback');
    } finally {
      setIsLoading(false);
    }
  };

  if (!email) {
    return (
      <div className={styles.container}>
        <div className={styles.card}>
          <div className={styles.errorMessage}>
            No recipient email provided in the URL.
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className={styles.container}>
      <div className={styles.card}>
        <h1 className={styles.title}>We appreciate your feedback</h1>
        <p className={styles.subtitle}>Please take a moment to tell us what you think</p>
        
        <div className={styles.recipientInfo}>
          <span className={styles.recipientLabel}>To: </span>
          <span className={styles.recipientEmail}>{email}</span>
        </div>

        {submitted ? (
          <div className={styles.successContainer}>
            <div className={styles.successIcon}>âœ“</div>
            <p className={styles.successMessage}>{status}</p>
          </div>
        ) : (
          <form onSubmit={handleSubmit} className={styles.form}>
            <div className={styles.inputGroup}>
              <label htmlFor="message" className={styles.label}>
                Your Feedback
              </label>
              <textarea
                id="message"
                placeholder="What can we do to improve your experience?"
                rows={5}
                className={styles.textarea}
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                required
              />
            </div>
            
            <button 
              type="submit" 
              className={styles.button}
              disabled={isLoading}
            >
              {isLoading ? 'Submitting...' : 'Submit My Feedback'}
            </button>
            
            {status && !submitted && (
              <p className={styles.errorStatus}>{status}</p>
            )}
          </form>
        )}
        
        <div className={styles.footer}>
          <span className={styles.footerText}>Powered by </span>
          <a href="#" className={styles.footerLink}>Incognito Inbox</a>
        </div>
      </div>
    </div>
  );
};

export default SendFeedback;
