import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Filter } from 'bad-words';

const SendFeedback = () => {
  const [email, setEmail] = useState('');
  const [subject, setSubject] = useState('');
  const [message, setMessage] = useState('');
  const [status, setStatus] = useState('');
  const [submitted, setSubmitted] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const userEmail = params.get('email');
    if (userEmail) {
      setEmail(userEmail);
      console.log('Email from URL:', userEmail);
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    console.log('=== FORM SUBMISSION DEBUG ===');
    console.log('Raw form values:', {
      email: email,
      subject: subject,
      message: message,
      subjectLength: subject.length,
      subjectTrimmed: subject.trim(),
      subjectTrimmedLength: subject.trim().length
    });

    if (!email) {
      setStatus('Email is required.');
      return;
    }

    if (!subject.trim()) {
      setStatus('Subject is required.');
      console.log('Subject validation failed - subject is empty after trim');
      return;
    }

    if (!message.trim()) {
      setStatus('Please enter a message.');
      return;
    }

    setIsLoading(true);
    setStatus('');

    try {
      const filter = new Filter();
      const cleanedMessage = filter.clean(message);
      const cleanedSubject = filter.clean(subject);

      const dataToSend = {
        email: email,
        subject: cleanedSubject,
        message: cleanedMessage,
      };

      console.log('=== DATA BEING SENT TO SERVER ===');
      console.log('Full payload:', dataToSend);
      console.log('Subject specifically:', {
        original: subject,
        cleaned: cleanedSubject,
        type: typeof cleanedSubject,
        length: cleanedSubject.length
      });

      const res = await axios.post('http://localhost:5000/api/feedback/send', dataToSend, {
        headers: {
          'Content-Type': 'application/json'
        }
      });

      console.log('=== SERVER RESPONSE ===');
      console.log('Full response:', res);
      console.log('Response data:', res.data);
      console.log('Response status:', res.status);

      setStatus(res.data.message);
      setSubmitted(true);
      
      // Don't clear form yet so we can debug
      // setSubject('');
      // setMessage('');
      
    } catch (error) {
      console.error('=== ERROR SENDING FEEDBACK ===');
      console.error('Full error:', error);
      console.error('Error response:', error.response);
      console.error('Error response data:', error.response?.data);
      setStatus(error.response?.data?.message || 'Failed to send feedback');
    } finally {
      setIsLoading(false);
    }
  };

  // Debug: Log state changes
  useEffect(() => {
    console.log('Subject state changed:', subject);
  }, [subject]);

  useEffect(() => {
    console.log('Message state changed:', message);
  }, [message]);

  if (!email) {
    return (
      <div style={{ padding: '20px', textAlign: 'center' }}>
        <div style={{ 
          backgroundColor: '#f8f9fa', 
          padding: '20px', 
          borderRadius: '8px',
          maxWidth: '600px',
          margin: '0 auto'
        }}>
          <div style={{ color: '#dc3545', fontSize: '18px' }}>
            No recipient email provided in the URL.
          </div>
          <p style={{ marginTop: '10px', color: '#6c757d' }}>
            Please use a valid feedback link with an email parameter.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div style={{ 
      minHeight: '100vh', 
      backgroundColor: '#f8f9fa', 
      padding: '20px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    }}>
      <div style={{ 
        backgroundColor: 'white', 
        padding: '40px', 
        borderRadius: '12px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
        maxWidth: '600px',
        width: '100%'
      }}>
        <h1 style={{ 
          fontSize: '28px', 
          fontWeight: 'bold', 
          marginBottom: '8px',
          color: '#212529'
        }}>
          We appreciate your feedback
        </h1>
        
        <p style={{ 
          color: '#6c757d', 
          marginBottom: '24px',
          fontSize: '16px'
        }}>
          Please take a moment to tell us what you think
        </p>

        <div style={{ 
          backgroundColor: '#e9ecef', 
          padding: '12px 16px', 
          borderRadius: '6px',
          marginBottom: '24px'
        }}>
          <span style={{ fontWeight: '500', color: '#495057' }}>To: </span>
          <span style={{ color: '#007bff' }}>{email}</span>
        </div>

        {/* Debug info */}
        <div style={{ 
          backgroundColor: '#fff3cd', 
          border: '1px solid #ffeaa7',
          padding: '10px', 
          marginBottom: '20px',
          borderRadius: '4px',
          fontSize: '12px'
        }}>
          <strong>Debug Info:</strong><br/>
          Subject: "{subject}" (Length: {subject.length})<br/>
          Message: "{message}" (Length: {message.length})
        </div>

        {submitted ? (
          <div style={{ textAlign: 'center', padding: '20px' }}>
            <div style={{ 
              fontSize: '48px', 
              color: '#28a745',
              marginBottom: '16px'
            }}>
              âœ“
            </div>
            <p style={{ 
              color: '#28a745', 
              fontSize: '18px',
              fontWeight: '500'
            }}>
              {status}
            </p>
            <button 
              onClick={() => {
                setSubmitted(false);
                setStatus('');
                setSubject('');
                setMessage('');
              }}
              style={{
                marginTop: '16px',
                padding: '8px 16px',
                backgroundColor: '#007bff',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              Send Another Feedback
            </button>
          </div>
        ) : (
          <form onSubmit={handleSubmit}>
            <div style={{ marginBottom: '20px' }}>
              <label htmlFor="subject" style={{ 
                display: 'block', 
                marginBottom: '8px',
                fontWeight: '500',
                color: '#212529'
              }}>
                Subject *
              </label>
              <input
                id="subject"
                name="subject"
                type="text"
                placeholder="Enter subject"
                style={{
                  width: '100%',
                  padding: '12px',
                  border: '1px solid #ced4da',
                  borderRadius: '6px',
                  fontSize: '16px',
                  boxSizing: 'border-box'
                }}
                value={subject}
                onChange={(e) => {
                  console.log('Subject input changed:', e.target.value);
                  setSubject(e.target.value);
                }}
                required
              />
            </div>

            <div style={{ marginBottom: '24px' }}>
              <label htmlFor="message" style={{ 
                display: 'block', 
                marginBottom: '8px',
                fontWeight: '500',
                color: '#212529'
              }}>
                Your Feedback *
              </label>
              <textarea
                id="message"
                name="message"
                placeholder="What can we do to improve your experience?"
                rows={5}
                style={{
                  width: '100%',
                  padding: '12px',
                  border: '1px solid #ced4da',
                  borderRadius: '6px',
                  fontSize: '16px',
                  resize: 'vertical',
                  boxSizing: 'border-box'
                }}
                value={message}
                onChange={(e) => {
                  console.log('Message input changed:', e.target.value);
                  setMessage(e.target.value);
                }}
                required
              />
            </div>

            <button 
              type="submit" 
              disabled={isLoading}
              style={{
                width: '100%',
                padding: '12px',
                backgroundColor: isLoading ? '#6c757d' : '#007bff',
                color: 'white',
                border: 'none',
                borderRadius: '6px',
                fontSize: '16px',
                fontWeight: '500',
                cursor: isLoading ? 'not-allowed' : 'pointer',
                transition: 'background-color 0.2s'
              }}
            >
              {isLoading ? 'Sending...' : 'Send Feedback'}
            </button>

            {status && !submitted && (
              <p style={{ 
                marginTop: '16px', 
                color: status.includes('Failed') || status.includes('required') ? '#dc3545' : '#28a745',
                textAlign: 'center',
                fontSize: '14px'
              }}>
                {status}
              </p>
            )}
          </form>
        )}
      </div>
    </div>
  );
};

export default SendFeedback;
