import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [otp, setOtp] = useState('');
  const [step, setStep] = useState(1);
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const sendOTP = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/auth/send-otp', { email });
      setMessage(response.data.message);
      setStep(2);
    } catch (error) {
      console.error('Send OTP Error:', error);
      setMessage(error.response?.data?.message || 'Error sending OTP');
    }
  };

  const verifyOTP = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/auth/verify-otp', { email, otp });
      setMessage(response.data.message);
      localStorage.setItem('email', email);
      navigate('/dashboard');
    } catch (error) {
      console.error('Verify OTP Error:', error);
      setMessage(error.response?.data?.message || 'Error verifying OTP');
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-[#9bbde0] font-serif text-[#2e2c2f]">
      <h1 className="text-3xl font-bold mb-8 text-center">Welcome to Incognito Inbox</h1>
      
      <div className="bg-[#d0dded] rounded-lg shadow-lg p-8 w-full max-w-md text-center">
        <h2 className="text-xl mb-6">Enter your email to continue</h2>

        <input
          type="email"
          placeholder="Enter your email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="w-full p-2 rounded border mb-4 focus:outline-none focus:ring-2 focus:ring-blue-300"
        />

        {step === 1 ? (
          <button
            onClick={sendOTP}
            className="bg-[#2e2c2f] text-white py-2 px-4 rounded hover:opacity-90"
          >
            Send OTP
          </button>
        ) : (
          <>
            <input
              type="text"
              placeholder="Enter OTP"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
              className="w-full p-2 rounded border mb-4 focus:outline-none focus:ring-2 focus:ring-blue-300"
            />
            <button
              onClick={verifyOTP}
              className="bg-[#2e2c2f] text-white py-2 px-4 rounded hover:opacity-90"
            >
              Verify OTP
            </button>
          </>
        )}

        {message && <p className="mt-4">{message}</p>}
      </div>
    </div>
  );
};

export default Login;
