import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [email, setEmail] = useState('');
  const [feedbacks, setFeedbacks] = useState([]);
  const [message, setMessage] = useState('');
  const [link, setLink] = useState('');

  useEffect(() => {
    const storedEmail = localStorage.getItem('email');
    if (storedEmail) {
      setEmail(storedEmail);
      fetchFeedback(storedEmail);
    } else {
      setMessage('No logged-in user. Please log in.');
    }
  }, []);

  const fetchFeedback = async (emailToFetch) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/feedback/${emailToFetch}`);
      setFeedbacks(response.data);
      setMessage('Feedbacks loaded successfully');
      setLink(`http://localhost:3000/send-feedback?email=${encodeURIComponent(emailToFetch)}`);
    } catch (error) {
      setMessage(error.response?.data?.message || 'Failed to load feedbacks');
    }
  };

  const handleCopy = () => {
    navigator.clipboard.writeText(link).then(() => {
      alert('Inbox URL copied!');
    });
  };

  return (
    <div className="min-h-screen bg-blue-100 flex items-center justify-center">
      <div className="bg-white p-8 rounded-2xl shadow-lg w-full max-w-2xl">
        <h2 className="text-2xl font-bold text-center mb-4">Dashboard</h2>

        <div className="mb-6">
          <p className="text-sm text-gray-600 mb-2">Your Inbox Link:</p>
          <div className="flex items-center space-x-2">
            <code className="text-sm bg-gray-100 px-2 py-1 rounded break-all flex-1">{link}</code>
            <button
              onClick={handleCopy}
              className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded-md"
            >
              Copy
            </button>
          </div>
        </div>

        <h3 className="text-lg font-semibold mb-3">Feedback Received</h3>
        {message && <p className="mb-4 text-sm text-gray-500">{message}</p>}

        {feedbacks.length === 0 ? (
          <p className="text-gray-500">No feedback yet.</p>
        ) : (
          <ul className="space-y-3">
            {feedbacks.map((fb) => (
              <li
                key={fb._id}
                className="p-4 bg-gray-50 border rounded-md shadow-sm text-sm text-gray-800"
              >
                {fb.message}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
