import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Filter } from 'bad-words';

const SendFeedback = () => {
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [status, setStatus] = useState('');
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const userEmail = params.get('email');
    setEmail(userEmail);
  }, []);

  const handleSubmit = async () => {
    if (!message.trim()) {
      setStatus('Please enter a message.');
      return;
    }

    const filter = new Filter();
    const cleanedMessage = filter.clean(message);

    try {
      const res = await axios.post('http://localhost:5000/api/feedback/send', {
        email,
        message: cleanedMessage,
      });
      setStatus(res.data.message);
      setSubmitted(true);
    } catch (error) {
      setStatus(error.response?.data?.message || 'Failed to send feedback');
    }
  };

  if (!email) {
    return <p className="text-center mt-10 text-red-500">No recipient email provided in the URL.</p>;
  }

  return (
    <div className="min-h-screen bg-blue-100 flex items-center justify-center">
      <div className="bg-white p-8 rounded-2xl shadow-lg w-full max-w-md">
        <h2 className="text-xl font-bold text-center mb-2">We appreciate your feedback.</h2>
        <p className="text-center text-sm text-gray-600 mb-4">
          Please take a moment to tell us what you think.
        </p>

        <p className="mb-4 text-center text-sm text-gray-800">
          To: <strong>{email}</strong>
        </p>

        {submitted ? (
          <p className="text-green-600 text-center">{status}</p>
        ) : (
          <>
            <textarea
              placeholder="What can we do to improve your experience?"
              rows={5}
              className="w-full p-3 border border-gray-300 rounded-md mb-4 resize-none focus:outline-none focus:ring-2 focus:ring-blue-400"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
            />
            <button
              onClick={handleSubmit}
              className="w-full bg-blue-600 hover:bg-blue-700 text-white py-2 rounded-md"
            >
              Submit My Feedback
            </button>
            {status && <p className="mt-3 text-center text-red-500">{status}</p>}
          </>
        )}
      </div>
    </div>
  );
};

export default SendFeedback;
