import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styles from './styles/Dashboard.module.css';


const Dashboard = () => {
  const [email, setEmail] = useState('');
  const [feedbacks, setFeedbacks] = useState([]);
  const [message, setMessage] = useState('');
  const [link, setLink] = useState('');

  useEffect(() => {
    const storedEmail = localStorage.getItem('email');
    if (storedEmail) {
      setEmail(storedEmail);
      fetchFeedback(storedEmail);
    } else {
      setMessage('No logged-in user. Please log in.');
    }
  }, []);

  const fetchFeedback = async (emailToFetch) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/feedback/${emailToFetch}`);
      setFeedbacks(response.data);
      setMessage('Feedbacks loaded successfully');
      setLink(`http://localhost:3000/send-feedback?email=${encodeURIComponent(emailToFetch)}`);
    } catch (error) {
      setMessage(error.response?.data?.message || 'Failed to load feedbacks');
    }
  };

  const handleCopy = () => {
    navigator.clipboard.writeText(link).then(() => {
      alert('Inbox URL copied!');
    });
  };

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        <h1 className={styles.title}>Incognito Inbox</h1>
      </div>
      
      <div className={styles.content}>
        <div className={styles.boxContainer}>
          {/* Inbox Link Box */}
          <div className={styles.box}>
            <h3 className={styles.boxTitle}>Your Inbox Link</h3>
            <div className={styles.linkContainer}>
              <code className={styles.linkCode}>{link}</code>
              <button onClick={handleCopy} className={styles.copyButton}>
                Copy
              </button>
            </div>
          </div>
          
          {/* Feedback Box */}
          <div className={styles.box}>
            <h3 className={styles.boxTitle}>Feedback Received</h3>
            {message && <p className={styles.message}>{message}</p>}
            
            <div className={styles.feedbackContainer}>
              {feedbacks.length === 0 ? (
                <p className={styles.noFeedback}>No feedback yet.</p>
              ) : (
                <ul className={styles.feedbackList}>
                  {feedbacks.map((fb) => (
                    <li key={fb._id} className={styles.feedbackItem}>
                      {fb.message}
                    </li>
                  ))}
                </ul>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
